#!/usr/bin/env perl

use strict;
use DBI;
use JSON qw/decode_json/;
use FindBin qw/$Bin/;
use open qw/:std :utf8/;

warn qq[==============================================================
This script insert pages, posts and images from the old Akkeri
WordPress site. The data is current as of 2016-01-10.
==============================================================
];

chdir $Bin;
die "Sanity check failed: No '2015' image directory\n" unless -d "2015";
die "Sanity check failed: No ~/akkeri-flask/static/images/ directory\n" unless -d "$ENV{HOME}/akkeri-flask/static/images";

$/ = undef;
my $data = decode_json <DATA>;

my $dbh = DBI->connect(
    'dbi:Pg:dbname=akkeri-'.$ENV{USER}, undef, undef,
    {AutoCommit=>1, RaiseError=>1})
        or die "Could not connect to database akkeri-$ENV{USER}\n";

unless ($dbh->selectrow_array("select id from users where id = 1")) {
    die "Sanity check failed: no user with id=1 in users table\n";
}

$dbh->begin_work;

eval {
    init_sequences('posts', 'images', 'x_post_image');
    my %xpi = insert_media($dbh, $data->{MEDIA});
    my %feat = insert_posts($dbh, $data->{PAGES}, 'page');
    my %feat2 = insert_posts($dbh, $data->{POSTS}, 'post');
    insert_x_post_image(\%xpi, {%feat, %feat2});
};

if ($@) {
    $dbh->rollback;
    die "FATAL ERROR: $@\n";
} else {
    $dbh->commit;
    system("rsync -a 2015 $ENV{HOME}/akkeri-flask/static/images/");
    warn "DONE\n";
}

########## SUBS BELOW

sub init_sequences {
    my @tables = @_;
    foreach my $table (@tables) {
        my $seqname = $table.'_id_seq';
        $dbh->do("select setval('$seqname', (select coalesce(max(id), 1) from $table))");
    }
}

sub insert_media {
    my ($dbh, $media) = @_;
    my %xpi = (); # map post_id => [img_id, ...]
    # date, post?, modified_gmt, slug, id,
    # media_details->width, media_details->height, media_details->file,
    # alt_text, description, title->rendered, caption
    my $sql = qq[
      insert into images
        (id, owner_id, image_path, title, caption,
         width, height, bytes,
         available_to_others, added, changed)
        values
        (?, 1, ?, ?, ?,
         ?, ?, ?,
         true, ?, ?)
    ];
    my $sth = $dbh->prepare($sql);
    foreach my $img (@$media) {
        my $md = $img->{media_details};
        my $bytes = -s $md->{file};
        my $cap = $img->{caption} || $img->{description} || $img->{alt_text};
        warn "- inserting image $img->{id}: $md->{file}\n";
        $sth->execute(
            $img->{id}, $md->{file}, $img->{title}->{rendered}, $cap,
            $md->{width}, $md->{height}, $bytes,
            $img->{date_gmt}, $img->{modified_gmt});
        my $post = $img->{post};
        if ($post) {
            $xpi{$post} ||= [];
            push @{$xpi{$post}}, $img->{id};
        }
    }
    $dbh->do("select setval('images_id_seq', (select max(id) from images))");
    return %xpi;
}

sub insert_posts {
    my ($dbh, $posts, $type) = @_;
    my %feat = (); # post_id => img_id
    my $type_id = $type eq 'page' ? 5 : 3; # page vs article
    # excerpt->rendered, content->rendered, modified_gmt,
    # featured_image (unless 0), slug, id, date_gmt,
    # title->rendered
    my $sql = qq[
      insert into posts
        (id, author_id, author_visible, title, slug,
         summary, body, post_type_id, language_id,
         created, changed, published)
        values
        (?, 1, false, ?, ?,
         ?, ?, $type_id, 2,
         ?, ?, ?)
    ];
    my $sth = $dbh->prepare($sql);
    foreach my $post (@$posts) {
        my $body = $post->{content}->{rendered};
        # minimal html cleanup
        $body =~ s{<span style[^>]+>(.*?)</span>}{$1}g;
        my $title = $post->{title}->{rendered};
        $feat{$post->{id}} = int($post->{featured_image});
        warn "- inserting $type $post->{id}: $title\n";
        $sth->execute(
            $post->{id}, $title, $post->{slug},
            $post->{excerpt}->{rendered}, $body,
            $post->{date_gmt}, $post->{modified_gmt}, $post->{date_gmt}
        );
    }
    $dbh->do("select setval('posts_id_seq', (select max(id) from posts))");
    return %feat;
}

sub insert_x_post_image {
    my ($xpi, $feat) = @_;
    my %seen = ();
    my %order = ();
    # Quick fix for missing data (draft post, not in JSON)
    my %unpublished = (12=>1);
    my $sth = $dbh->prepare(
        "insert into x_post_image (post_id, image_id, image_order) values (?, ?, ?)"
    );
    foreach my $post_id (keys %$xpi) {
        my $feat_key = "$post_id x ".$feat->{$post_id} if $feat->{$post_id};
        if ($feat_key && !$seen{$feat_key} && $feat->{$post_id} > 0) {
            warn "- inserting x_post_image: post_id=$post_id, img_id=$feat->{$post_id} (order=1)\n";
            $sth->execute($post_id, $feat->{$post_id}, 1);
            $order{$post_id} = 2;
            $seen{$feat_key}++;
        } else {
            $order{$post_id} ||= 1;
        }
        my @img_ids = @{ $xpi->{$post_id} };
        foreach my $img_id (@img_ids) {
            if ($unpublished{$post_id}) {
                warn "WARNING: Cannot associate image $img_id with post $post_id: post not in dataset\n";
                next;
            }
            my $key = "$post_id x $img_id";
            next if $seen{$key}++;
            warn "- inserting x_post_image: post_id=$post_id, img_id=$img_id (order=$order{$post_id})\n";
            $sth->execute($post_id, $img_id, $order{$post_id}++);
        }
    }
    # There might be featured images that were not present in $xpi
    foreach my $post_id (keys %$feat) {
        next unless $feat->{$post_id} > 0;
        my $feat_key = "$post_id x ".$feat->{$post_id};
        unless ($seen{$feat_key}) {
            warn "- inserting x_post_image: post_id=$post_id, img_id=$feat->{$post_id} (order=1)\n";
            $sth->execute($post_id, $feat->{$post_id}, 1);
        }
    }
}


